mapper.update:
update review_board
set review_title = #{review_title},
review_content = #{review_content}
where review_num = #{review_num}

update review_board 
set reply_count = reply_count + #{amount}
where review_num = #{review_num}

update review_board
set review_read_count = review_read_count + 1
where review_num = #{review_num}
