mapper.select:
	
 			SELECT BS_NUM, CATEGORY, BOARDNUM, B_MEMBER_ID, MEMBER_ID, BS_CONTENT, BS_REPLY, B_REG_DATE, B_MANAGE_DATE
 			  FROM (SELECT /*+INDEX_DESC(BLOCK_STATUS BLOCKNUM) */ rownum as rNum, BS_NUM, CATEGORY, BOARDNUM, B_MEMBER_ID, MEMBER_ID, BS_CONTENT, BS_REPLY, B_REG_DATE, B_MANAGE_DATE 
 					  FROM BLOCK_STATUS 
 					 WHERE rownum <= #{pageNum} * #{amount}
 					 ORDER BY BS_NUM DESC)
 			 WHERE rNum > (#{pageNum} - 1) * #{amount}

	
 			SELECT BS_NUM, CATEGORY, BOARDNUM, B_MEMBER_ID, MEMBER_ID, BS_CONTENT, BS_REPLY, B_REG_DATE, B_MANAGE_DATE
 			  FROM (SELECT /*+INDEX_DESC(BLOCK_STATUS BLOCKNUM) */ rownum as rNum, BS_NUM, CATEGORY, BOARDNUM, B_MEMBER_ID, MEMBER_ID, BS_CONTENT, BS_REPLY, B_REG_DATE, B_MANAGE_DATE 
 					  FROM BLOCK_STATUS 
 					 WHERE rownum <= #{cri.pageNum} * #{cri.amount} AND MEMBER_ID = #{memId}
 					 ORDER BY BS_NUM DESC)
 			 WHERE rNum > (#{cri.pageNum} - 1) * #{cri.amount}
