mapper.select:

	select mention_no,exh_seq,member_id,mention_content,mention_score,mention_regdate
	from(select /*+INDEX_DESC(m index_mention_no) */ rownum rn,  mention_no,exh_seq,member_id,mention_content,mention_score,mention_regdate
	from(select mention_no, exh_seq,member_id,mention_content,mention_score,mention_regdate from exhibition_mention m where exh_seq = #{exh_seq} )
	where rownum <= #{cri.pageNum} * #{cri.amount}) where rn > (#{cri.pageNum} -1) * #{cri.amount}
